    public function me($key) {
        session_start() ; 
        if(isset($_SESSION['user'])) {
            return $_SESSION['user'][$key] ;
        }
    }
    
    public function index() {
       
        $my_id = $this->me('id') ; 
        $with_id = @$_SESSION['chatWith'];
        $chat = DB::table('chat')
        ->where('from_id' , $with_id)
        ->where('to_id' , $my_id)
        ->orWhere('from_id' , $my_id)
        ->where('to_id' , $with_id)
        ->get();
    
        $emojis = $this->emojis() ; 
        $get_user_by_id = function($id , $key) {
            $select = DB::table('users')->where('id' , $id)->first() ;
            if($select) {
                return $select -> $key ;
            }
            return 'Whatswrap user' ; 
        };
        
        $contact = DB::table('contact')->where('owner_id' , $my_id)->orWhere('contact_id' , $my_id)->get() ; 

        return view('welcome')
        ->with('get_user' , $get_user_by_id)
        ->with('emojis' , $emojis)
        ->with('chat' , $chat)
        ->with('contact' , $contact);
        
    }
    public function refresh_contact() {
  
        $my_id = $this->me('id') ; 
        $with_id = $_SESSION['chatWith'];
        $chat = DB::table('chat')
        ->where('from_id' , $with_id)
        ->where('to_id' , $my_id)
        ->orWhere('from_id' , $my_id)
        ->where('to_id' , $with_id)
        
        ->get();
    
        $emojis = $this->emojis() ; 
        $get_user_by_id = function($id , $key) {
            $select = DB::table('users')->where('id' , $id)->first() ;
            if($select) {
                return $select -> $key ;
            }

            return 'Whatswrap user' ; 
        };
        
        $contact = DB::table('contact')->where('owner_id' , $my_id)->orWhere('contact_id' , $my_id)->get() ; 

        return view('component.contact')
        ->with('get_user' , $get_user_by_id)
        ->with('emojis' , $emojis)
        ->with('chat' , $chat)
        ->with('contact' , $contact);
        
    }
    public function refresh_chat() {
        $my_id = $this->me('id') ; 
        $with_id = $_SESSION['chatWith'];
        $chat = DB::table('chat')
        ->where('from_id' , $with_id)
        ->where('to_id' , $my_id)
        ->orWhere('from_id' , $my_id)
        ->where('to_id' , $with_id)
        ->get();
    
        $emojis = $this->emojis() ; 
        $get_user_by_id = function($id , $key) {
            $select = DB::table('users')->where('id' , $id)->first() ;
            if($select) {
                return $select -> $key ;
            }

            return 'Whatswrap user' ; 
        };
        
        $contact = DB::table('contact')->where('owner_id' , $my_id)->orWhere('contact_id' , $my_id)->get() ; 

        return view('component.chat.content')
        ->with('get_user' , $get_user_by_id)
        ->with('emojis' , $emojis)
        ->with('chat' , $chat)
        ->with('contact' , $contact);
    }
    public function chat_with(Request $request) {
        $with_id = $request -> id ;
        session_start() ;
        
        
        $_SESSION['chatWith'] = $with_id ; 
        
        return '<script>location.reload()</script>' ;
        
        
       
        
    }
    public function close() {
        session_start() ; 
        $_SESSION['chatWith'] = null ; 
        return '<script>location.reload()</script>' ;
    }
    public function message(Request $request) {
        session_start() ; 
        $me = $_SESSION['user']['id'];
        $to = $_SESSION['chatWith'];
        $message = $request -> text ;
        $media = 'none' ; 
        $media_type = 'text' ;
        $room = 0 ; 
        $insert = DB::table('chat')->insert([
            'for_room_id' => $room ,
            'from_id'  =>  $me , 
            'to_id'  => $to ,
            'text' => $message , 
            'media_type' => $media_type ,
            'media' => $media 
        ]);

    }
    public function search_contact(Request $request) {
        $search = $request -> search ; 
        session_start();
        $check_search = DB::table('users')->where('username', 'like', '%'.$search.'%')->whereNot('username' , $_SESSION['user']['username'])->get() ; 
        if($check_search) {

            return view('component.extensions.search')->with('search_result' , $check_search) ; 
        }else{
            return `<div id="empty" class="flex justify-center items-center w-full h-full">
            <p class="text-gray-300">No Recent Search</p>
        </div>` ; 
        }
    }
